package org.cassproject.ebac.identity;

import com.eduworks.ec.crypto.EcAes;
import com.eduworks.ec.crypto.EcPpk;
import com.eduworks.schema.ebac.EbacContact;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.stjs.testing.annotation.ScriptsBefore;
import org.stjs.testing.driver.STJSTestDriverRunner;

import static org.junit.Assert.assertTrue;
import static org.stjs.javascript.Global.console;

@RunWith(STJSTestDriverRunner.class)
@ScriptsBefore({"/forge/forge.bundle.js"})
public class EcContactTest {
	@Test
	public void encryptContactTest() {
		EcPpk ppk = EcPpk
				.fromPem("-----BEGIN RSA PRIVATE KEY----------END RSA PRIVATE KEY-----");
		EcContact contact = new EcContact();
		contact.pk = ppk.toPk();
		contact.displayName = "Test Person";
		contact.source = "http://sandbox.service.cassproject.org";
		String secret = EcAes.newIv(32);
		console.log("Secret: " + secret);
		console.log("Encrypting contact...");
		EbacContact encryptedContact = contact.toEncryptedContact(secret);
		console.log("Decrypting contact...");
		EcContact decryptedContact = EcContact.fromEncryptedContact(encryptedContact, secret, contact.source);
		console.log("OK.");
		assertTrue(contact.pk.toPem().equals(decryptedContact.pk.toPem()));
		assertTrue(contact.displayName.equals(decryptedContact.displayName));
	}
}
